mapper.select:
SELECT subscription_id, user_id, product_id, subscription_status, start_date, end_date, cancel_reason, cancel_date
FROM subscription
WHERE subscription_id = #{subscriptionId}

mapper.update:
UPDATE subscription
SET subscription_status = #{subscriptionStatus},
    end_date = #{endDate},
    cancel_reason = #{cancelReason},
    cancel_date = #{cancelDate}
WHERE subscription_id = #{subscriptionId}
